import React,{useState,useEffect} from "react";
import "./styles.css";



function getTransactionsFromLocalStorage() {
  // getting stored value
  const saved = localStorage.getItem("transactions");
  const initialValue = JSON.parse(saved)||[];
  // relabel the keys from 0 to length-1
  for(let i=0;i<initialValue.length;i++){
    initialValue[i]['key']=i
  }
  return initialValue || [];
}

function testing(){
    console.log("This is a test!")
}


export default function Transaction() {

  let [transactions, setTransactions] = useState(getTransactionsFromLocalStorage);
  let [numKeys,setNumKeys] = useState(() => transactions.length)
  let [summaryData, setSummary] = useState(null);
  let [msg,setMsg] = useState('none');

  function add_transaction() {
    const itemId = document.getElementById("itemId").value;
    const amount = document.getElementById("amount").value;
    const category = document.getElementById("category").value;
    const date = document.getElementById("date").value;
    const description = document.getElementById("description").value;

    let newTransaction = {
      key: numKeys,
      itemId,
      amount,
      category,
      date,
      description,
    };
    document.getElementById("itemId").value = "";
    document.getElementById("amount").value = "";
    document.getElementById("category").value = "";
    document.getElementById("date").value = "";
    document.getElementById("description").value = "";
    setNumKeys(numKeys+1)
    setTransactions([newTransaction,...transactions]); // using the spread operator ...
  }

  function deleteTransaction(key){
    console.log(key)
    const newTransaction = transactions.filter((x)=> x['key']!== key)
    setTransactions(newTransaction);
    setNumKeys(numKeys - 1);
  }

  function summarizeByDate(transactions) {
    const summary = {};
    transactions.forEach((transaction) => {
      const date = transaction.date;
      if (summary[date]) {
        summary[date].push(transaction);
      } else {
        summary[date] = [transaction];
      }
    });
    return summary;
  }

  function summarizeByMonth(transactions) {
    const summary = {};
    transactions.forEach((transaction) => {
      const month = transaction.date.slice(0, 7);
      if (summary[month]) {
        summary[month].push(transaction);
      } else {
        summary[month] = [transaction];
      }
    });
    return summary;
  }

  function summarizeByYear(transactions) {
    const summary = {};
    transactions.forEach((transaction) => {
      const year = transaction.date.slice(0, 4);
      if (summary[year]) {
        summary[year].push(transaction);
      } else {
        summary[year] = [transaction];
      }
    });
    return summary;
  }

  function summarizeByCategory(transactions) {
    const summary = {};
    transactions.forEach((transaction) => {
      const category = transaction.category;
      if (summary[category]) {
        summary[category].push(transaction);
      } else {
        summary[category] = [transaction];
      }
    });
    return summary;
  }

  function renderSummary() {
    if (!summaryData) {
      return <div>No summary data available.</div>;
    }

    return (
      <table className="summary-table">
        <thead>
          <tr>
            <th>{summaryData.label}</th>
            <th>Amount</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(summaryData.data).map(([key, value]) => (
            <tr key={key}>
              <td>{key}</td>
              <td>{value}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }




  useEffect(() => {
    // storing items if items changes value
    localStorage.setItem("transactions", JSON.stringify(transactions));
  }, [transactions]);

  // // demo of how to get data from an Express server
  // useEffect(() => {
  //   const getMsg = async () => {
  //     const response = await fetch('http://localhost:3000/test');
  //     const result = await response.json();
  //     setMsg(result);
  //     console.log('msg =',result);
  //   }
  //   getMsg()
  // },[msg])

  // this is used to allow text data to be submitted
  // when the user hits the 'Enter' key
  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      // ðŸ‘‡ Get input value
      add_transaction();
    }
  };



  return (
    <div className="App container">
      <h1 className="bg-warning text-center p-2">Transaction Tracker</h1>
      <h3>List of your Transactions</h3>
      <table className="transaction-table">
        <thead>
          <tr>
          <th>Delete</th>
          <th>ID</th>
          <th>Amount</th>
          <th>Category</th>
          <th>Date</th>
          <th>Description</th>
          </tr>
        </thead>
        <tbody>
            {transactions.map((transaction) => (
            <tr>       
              <td><button onClick={()=>deleteTransaction(transaction["key"])}>X</button></td>
              <td>{transaction.itemId}</td>
              <td>{transaction.amount}</td>
              <td>{transaction.category}</td>
              <td>{transaction.date}</td>
              <td>{transaction.description}</td>
            </tr>
            ))}
        </tbody>
      </table>
      <button onClick={() => setSummary(summarizeByDate(transactions))}>Summarize by Date
      </button>
      <button onClick={() => setSummary(summarizeByMonth(transactions))}>Summarize by Month
      </button>
      <button onClick={() => setSummary(summarizeByYear(transactions))}>Summarize by Year
      </button>
      <button onClick={() => setSummary(summarizeByCategory(transactions))}>Summarize by Category
      </button>

      <h2>Summary</h2>
      <div>{renderSummary()}</div>

      <h2> Add new transaction item </h2>
      <input type="number" id="itemId" placeholder="Item ID" onKeyDown={handleKeyDown} />
      <input type="number" id="amount" placeholder="Amount" onKeyDown={handleKeyDown} />
      <input type="text" id="category" placeholder="Category" onKeyDown={handleKeyDown} />
      <input type="date" id="date" placeholder="Date" onKeyDown={handleKeyDown} />
      <input type="text" id="description" placeholder="Description" onKeyDown={handleKeyDown} />
      <button onClick={() => add_transaction()}>Add Transaction</button>

      <h2> DEBUGGING: list of items in JSON </h2>
      <pre>
       {JSON.stringify(transactions, null, 5)}
      </pre>
    </div>

  );
}
